svg.script:
	//

	function inCircle(clickX, clickY, circleX, circleY, radius) {
		return (Math.pow(clickX-circleX,2) + Math.pow(clickY-circleY,2) < Math.pow(radius,2));
	}

	function inOverlap(event) {
		if (inCircle(event.clientX, event.clientY, 125, 125, 120) && inCircle(event.clientX, event.clientY, 240, 125, 120)) {
			console.log("design clicked");
			parent.window.location.href = '/projects';
		}
	}

	function inDiagram(event) {
		return (inCircle(event.clientX, event.clientY, 125, 125, 120) || inCircle(event.clientX, event.clientY, 240, 125, 120));
	}

	function vennMoused(event) {
		console.log("mouseover occurred");
		if (inDiagram(event)) {
			console.log("in diagram");
			event.target.style.cursor = "pointer";
		}
		if (inCircle(event.clientX, event.clientY, 125, 125, 120)) {
			console.log("in code")
			document.getElementById("code-circle").setAttribute('fill-opacity', '0.5');
		} else {
			document.getElementById("code-circle").setAttribute('fill-opacity', '0.25');
		}

		if (inCircle(event.clientX, event.clientY, 240, 125, 120)) {
			document.getElementById("people-circle").setAttribute('fill-opacity', '0.5');
		} else {
			document.getElementById("people-circle").setAttribute('fill-opacity', '0.25');
		}
	}

	function vennClicked(event) {
		if (inOverlap(event)) {
			//design clicked
			parent.window.location.href = '/projects';
		} else if (inCircle(event.clientX, event.clientY, 125, 125, 120)) {
			//code circle clicked
			parent.window.location.href = '/projects';
		} else if (inCircle(event.clientX, event.clientY, 240, 125, 120)) {
			//people circle clicked
			parent.window.location.href = '/projects'; 
		}
	}

//
